mapper.sql:
FROM infrBanner a
WHERE 1=1
AND a.ifbnUseNy = #{shUseNy}
AND a.ifbnDelNy = #{shDelNy}


    AND a.ifbnSeq = #{shValue}
    AND a.ifbnTitle LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
		INSERT INTO infrBanner (
            ifbnLocationCd
            ,ifbnTitle
            ,ifbnUrl
            ,ifbnOrder
            ,ifbnUseNy
            ,ifbnDesc
            ,ifbnDelNy

		) VALUES (
			#{ifbnLocationCd}
			,#{ifbnTitle}
			,#{ifbnUrl}
			,#{ifbnOrder}
			,#{ifbnUseNy}
			,#{ifbnDesc}
			,0

		 )

			SELECT last_insert_id()

mapper.update:
UPDATE infrBanner
SET
	ifbnLocationCd = #{ifbnLocationCd}
	,ifbnTitle = #{ifbnTitle}
	,ifbnUrl = #{ifbnUrl}
	,ifbnOrder = #{ifbnOrder}
	,ifbnUseNy = #{ifbnUseNy}
	,ifbnDesc = #{ifbnDesc}

WHERE 1=1
	AND ifbnSeq = #{ifbnSeq}

mapper.delete:
DELETE FROM infrBanner 
WHERE 1=1
	AND ifbnSeq = #{ifbnSeq}
