mapper.sql:
        FROM infrNotice a
        WHERE 1=1
        AND a.ifntDelNy = #{shDelNy}


					AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.ifntTopDateStart BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.ifntTopDateEnd BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.ifntSeq = #{shValue}
            AND a.ifntTitle LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
		INSERT INTO infrNotice (
            ifntTitle
            ,ifntContents
            ,ifntTopOrder
            ,ifntTopDateStart
            ,ifntTopDateEnd
            ,ifntDesc
            ,ifntDelNy
            ,ifntDisplayNy
            ,ifntTopNy
            ,ifntTargetCd

		) VALUES (
			#{ifntTitle}
			,#{ifntContents}
			,#{ifntTopOrder}

				,NULL  
				,#{ifntTopDateStart}


				,NULL  
				,#{ifntTopDateEnd}

			,#{ifntDesc}
			,0
			,#{ifntDisplayNy}
			,#{ifntTopNy}
			,#{ifntTargetCd}

		 )

			SELECT last_insert_id()

mapper.update:
	UPDATE infrNotice
	SET
		ifntTitle = #{ifntTitle}
        ,ifntContents = #{ifntContents}
        ,ifntTopOrder = #{ifntTopOrder}
        ,ifntDesc = #{ifntDesc}
        ,ifntTopDateStart = 

			NULL  
			#{ifntTopDateStart}

        ,ifntTopDateEnd = 

			NULL  
			#{ifntTopDateEnd}

        ,ifntTargetCd = #{ifntTargetCd}

	WHERE 1=1
		AND ifntSeq = #{ifntSeq}

mapper.delete:
DELETE FROM infrNotice 
WHERE 1=1
	AND ifntSeq = #{ifntSeq}
