mapper.sql:
        FROM infrQna a
        WHERE 1=1

        AND a.ifqaDelNy = #{shDelNy}


					AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.ifqaSeq = #{shValue}
            AND a.ifqaTitle LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
		INSERT INTO infrQna (
            ifqaTitle
            ,ifqaTypeCd
            ,ifqaStatusCd
            ,ifqaContents
            ,ifqaAnswer
            ,ifqaAnswerSeq
            ,ifqaAnswerDateTime
            ,ifqaAnswerDisplayNy
            ,ifqaDelNy

		) VALUES (
			#{ifqaTitle}
			,#{ifqaTypeCd}
			,#{ifqaStatusCd}
			,#{ifqaContents}
			,#{ifqaAnswer}
			,#{ifqaAnswerSeq}
			,#{ifqaAnswerDateTime}
			,#{ifqaAnswerDisplayNy}
			,0

		 )

			SELECT last_insert_id()

mapper.update:
	UPDATE infrQna
	SET
		ifqaTitle = #{ifqaTitle}
        ,ifqaTypeCd = #{ifqaTypeCd}
        ,ifqaStatusCd = #{ifqaStatusCd}
        ,ifqaContents = #{ifqaContents}
        ,ifqaAnswer = #{ifqaAnswer}
        ,ifqaAnswerSeq = #{ifqaAnswerSeq}
        ,ifqaAnswerDateTime = #{ifqaAnswerDateTime}
        ,ifqaAnswerDisplayNy = #{ifqaAnswerDisplayNy}

	WHERE 1=1
		AND ifqaSeq = #{ifqaSeq}

mapper.delete:
DELETE FROM infrQna 
WHERE 1=1
	AND ifqaSeq = #{ifqaSeq}
