mapper.sql:
        FROM infrRule a
        WHERE 1=1
        AND a.ifrlUseNy = #{shUseNy}
        AND a.ifrlDelNy = #{shDelNy}


					AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.ifrlAnnounceDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.ifrlApplicationDateTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.ifrlSeq = #{shValue}
            AND a.ifrlTitle LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrRule (
	ifrlTitle
	,ifrlDesc
	,ifrlVersion
	,ifrlUseNy
	,ifrlApplicationDateTime
	,ifrlAnnounceDateTime
	,ifrlDelNy

) VALUES (
	#{ifrlTitle}
	,#{ifrlDesc}
	,#{ifrlVersion}
	,#{ifrlUseNy}
	,#{ifrlApplicationDateTime}
	,#{ifrlAnnounceDateTime}
	,0

 )

	SELECT last_insert_id()

mapper.update:
UPDATE infrRule
SET
	ifrlTitle = #{ifrlTitle}
	,ifrlDesc = #{ifrlDesc}
	,ifrlVersion = #{ifrlVersion}
	,ifrlUseNy = #{ifrlUseNy}
	,ifrlAnnounceDateTime = #{ifrlAnnounceDateTime}
	,ifrlApplicationDateTime = #{ifrlApplicationDateTime}

WHERE 1=1
	AND ifrlSeq = #{ifrlSeq}

mapper.delete:
DELETE FROM infrRule 
WHERE 1=1
	AND ifrlSeq = #{ifrlSeq}
